var fbVisApprovals=new Class({Implements:[Options],options:{},initialize:function(b,a){this.setOptions(a);head.ready(function(){this.el=document.id(b);document.addEvent("click:relay(a.approve)",function(f){var d=f.target;f.stop();if(d.get("tag")!=="a"){d=d.findUp("a")}var c=d.getParent(".floating-tip").retrieve("trigger");new Request.HTML({update:c,url:d.href}).send()});document.addEvent("click:relay(a.disapprove)",function(f){var d=f.target;if(d.get("tag")!=="a"){d=d.findUp("a")}var c=d.getParent(".floating-tip").retrieve("trigger");new Request.HTML({update:c,url:d.href}).send();f.stop()});new FloatingTips(".approvalTip",{position:"right",content:function(d){var c=d.getNext();c.store("trigger",d);return c},hideOn:"mousedown"})}.bind(this))}});