var fabrikFusiongraph=new Class({Implements:[Options],options:{legend:false,label:"",aChartKeys:{},axis_label:"",json:{},chartType:"Column3D",xticks:[]},initialize:function(b,c,a){this.el=b;this.setOptions(a);this.json=c;head.ready(function(){this.render()}.bind(this))},render:function(){switch(this.options.chartType){case"Column3D":this.graph=new Plotr.BarChart(this.el,this.options);break;case"PieChart":this.graph=new Plotr.PieChart(this.el,this.options);break;case"LineChart":this.graph=new Plotr.LineChart(this.el,this.options);break}this.graph.addDataset(this.json);this.graph.render();if(this.options.legend==="1"){this.graph.addLegend(this.el)}}});